import { tmpdir } from 'os';
import { promisify } from 'util';
import Queue from 'bull/lib/queue';
import { v4 as uuidv4 } from 'uuid';
import {
  mkdir, writeFile, stat, existsSync, realpath,
} from 'fs';
import { join as joinPath } from 'path';
import { Request, Response } from 'express';
import { contentType } from 'mime-types';
import mongoDBCore from 'mongodb/lib/core';
import dbClient from '../utils/db';
import { getUserFromXToken } from '../utils/auth';

const VALID_FILE_TYPES = {
  folder: 'folder',
  file: 'file',
  image: 'image',
};
const ROOT_FOLDER_ID = 0;
const DEFAULT_ROOT_FOLDER = 'files_manager';
const mkDirAsync = promisify(mkdir);
const writeFileAsync = promisify(writeFile);
const statAsync = promisify(stat);
const realpathAsync = promisify(realpath);
const MAX_FILES_PER_PAGE = 20;
const fileQueue = new Queue('thumbnail generation');
const NULL_ID = Buffer.alloc(24, '0').toString('utf-8');
const isValidId = (id) => {
  const size = 24;
  let i = 0;
  const charRanges = [
    [48, 57],
    [97, 102],
    [65, 70],
  ];
  if (typeof id !== 'string' || id.length !== size) {
    return false;
  }
  while (i < size) {
    const c = id[i];
    const code = c.charCodeAt(0);

    if (!charRanges.some((range) => code >= range[0] && code <= range[1])) {
      return false;
    }
    i += 1;
  }
  return true;
};

export default class FilesController {
  static async postUpload(req, res) {
    try {
      const { user } = req;
      const name = req.body ? req.body.name : null;
      const type = req.body ? req.body.type : null;
      const parentId = req.body && req.body.parentId ? req.body.parentId : ROOT_FOLDER_ID;
      const isPublic = req.body && req.body.isPublic ? req.body.isPublic : false;
      const base64Data = req.body && req.body.data ? req.body.data : '';

      if (!name) {
        res.status(400).json({ error: 'Missing name' });
        return;
      }
      if (!type || !Object.values(VALID_FILE_TYPES).includes(type)) {
        res.status(400).json({ error: 'Missing type' });
        return;
      }
      if (!req.body.data && type !== VALID_FILE_TYPES.folder) {
        res.status(400).json({ error: 'Missing data' });
        return;
      }

      const filesCollection = await dbClient.filesCollection();

      if ((parentId !== ROOT_FOLDER_ID) && (parentId !== ROOT_FOLDER_ID.toString())) {
        const file = await filesCollection.findOne({
          _id: new mongoDBCore.BSON.ObjectId(isValidId(parentId) ? parentId : NULL_ID),
        });

        if (!file) {
          res.status(400).json({ error: 'Parent not found' });
          return;
        }
        if (file.type !== VALID_FILE_TYPES.folder) {
          res.status(400).json({ error: 'Parent is not a folder' });
          return;
        }
      }

      const userId = user._id.toString();
      const baseDir = `${process.env.FOLDER_PATH || ''}`.trim().length > 0
        ? process.env.FOLDER_PATH.trim()
        : joinPath(tmpdir(), DEFAULT_ROOT_FOLDER);

      await mkDirAsync(baseDir, { recursive: true });

      const newFile = {
        userId: new mongoDBCore.BSON.ObjectId(userId),
        name,
        type,
        isPublic,
        parentId: (parentId === ROOT_FOLDER_ID) || (parentId === ROOT_FOLDER_ID.toString())
          ? '0'
          : new mongoDBCore.BSON.ObjectId(parentId),
      };

      if (type !== VALID_FILE_TYPES.folder) {
        const localPath = joinPath(baseDir, uuidv4());
        await writeFileAsync(localPath, Buffer.from(base64Data, 'base64'));
        newFile.localPath = localPath;
      }

      const insertionInfo = await filesCollection.insertOne(newFile);
      const fileId = insertionInfo.insertedId.toString();

      if (type === VALID_FILE_TYPES.image) {
        const jobName = `Image thumbnail [${userId}-${fileId}]`;
        fileQueue.add({ userId, fileId, name: jobName });
      }

      res.status(201).json({
        id: fileId,
        userId,
        name,
        type,
        isPublic,
        parentId: (parentId === ROOT_FOLDER_ID) || (parentId === ROOT_FOLDER_ID.toString())
          ? 0
          : parentId,
      });
    } catch (error) {
      console.error('Error in postUpload:', error);
      res.status(500).json({ error: 'Internal server error' });
    }
  }

  static async getShow(req, res) {
    try {
      const { user } = req;
      const id = req.params ? req.params.id : NULL_ID;
      const userId = user._id.toString();

      const filesCollection = await dbClient.filesCollection();
      const file = await filesCollection.findOne({
        _id: new mongoDBCore.BSON.ObjectId(isValidId(id) ? id : NULL_ID),
        userId: new mongoDBCore.BSON.ObjectId(isValidId(userId) ? userId : NULL_ID),
      });

      if (!file) {
        res.status(404).json({ error: 'Not found' });
        return;
      }

      res.status(200).json({
        id,
        userId,
        name: file.name,
        type: file.type,
        isPublic: file.isPublic,
        parentId: file.parentId === ROOT_FOLDER_ID.toString()
          ? 0
          : file.parentId.toString(),
      });
    } catch (error) {
      console.error('Error in getShow:', error);
      res.status(500).json({ error: 'Internal server error' });
    }
  }

  static async getIndex(req, res) {
    try {
      const { user } = req;
      const parentId = req.query.parentId || ROOT_FOLDER_ID.toString();
      const page = /\d+/.test((req.query.page || '').toString())
        ? Number.parseInt(req.query.page, 10)
        : 0;

      const filesCollection = await dbClient.filesCollection();
      const files = await filesCollection.aggregate([
        { $match: { userId: user._id } },
        { $sort: { _id: -1 } },
        { $skip: page * MAX_FILES_PER_PAGE },
        { $limit: MAX_FILES_PER_PAGE },
        {
          $project: {
            _id: 0,
            id: '$_id',
            userId: '$userId',
            name: '$name',
            type: '$type',
            isPublic: '$isPublic',
            parentId: {
              $cond: { if: { $eq: ['$parentId', '0'] }, then: 0, else: '$parentId' },
            },
          },
        },
      ]).toArray();

      res.status(200).json(files);
    } catch (error) {
      console.error('Error in getIndex:', error);
      res.status(500).json({ error: 'Internal server error' });
    }
  }

  static async putPublish(req, res) {
    try {
      const { user } = req;
      const { id } = req.params;

      const filesCollection = await dbClient.filesCollection();
      const fileFilter = {
        _id: new mongoDBCore.BSON.ObjectId(isValidId(id) ? id : NULL_ID),
        userId: new mongoDBCore.BSON.ObjectId(user._id.toString()),
      };

      const file = await filesCollection.findOne(fileFilter);
      if (!file) {
        res.status(404).json({ error: 'Not found' });
        return;
      }

      await filesCollection.updateOne(fileFilter, { $set: { isPublic: true } });

      res.status(200).json({
        id,
        userId: user._id.toString(),
        name: file.name,
        type: file.type,
        isPublic: true,
        parentId: file.parentId === ROOT_FOLDER_ID.toString()
          ? 0
          : file.parentId.toString(),
      });
    } catch (error) {
      console.error('Error in putPublish:', error);
      res.status(500).json({ error: 'Internal server error' });
    }
  }

  static async putUnpublish(req, res) {
    try {
      const { user } = req;
      const { id } = req.params;

      const filesCollection = await dbClient.filesCollection();
      const fileFilter = {
        _id: new mongoDBCore.BSON.ObjectId(isValidId(id) ? id : NULL_ID),
        userId: new mongoDBCore.BSON.ObjectId(user._id.toString()),
      };

      const file = await filesCollection.findOne(fileFilter);
      if (!file) {
        res.status(404).json({ error: 'Not found' });
        return;
      }

      await filesCollection.updateOne(fileFilter, { $set: { isPublic: false } });

      res.status(200).json({
        id,
        userId: user._id.toString(),
        name: file.name,
        type: file.type,
        isPublic: false,
        parentId: file.parentId === ROOT_FOLDER_ID.toString()
          ? 0
          : file.parentId.toString(),
      });
    } catch (error) {
      console.error('Error in putUnpublish:', error);
      res.status(500).json({ error: 'Internal server error' });
    }
  }

  static async getFile(req, res) {
    try {
      const user = await getUserFromXToken(req);
      const { id } = req.params;
      const size = req.query.size || null;
      const userId = user ? user._id.toString() : '';

      const filesCollection = await dbClient.filesCollection();
      const fileFilter = {
        _id: new mongoDBCore.BSON.ObjectId(isValidId(id) ? id : NULL_ID),
      };

      const file = await filesCollection.findOne(fileFilter);

      if (!file || (!file.isPublic && (file.userId.toString() !== userId))) {
        res.status(404).json({ error: 'Not found' });
        return;
      }

      if (file.type === VALID_FILE_TYPES.folder) {
        res.status(400).json({ error: 'A folder doesn\'t have content' });
        return;
      }

      let filePath = file.localPath;
      if (size) {
        filePath = `${file.localPath}_${size}`;
      }

      if (existsSync(filePath)) {
        const fileInfo = await statAsync(filePath);
        if (!fileInfo.isFile()) {
          res.status(404).json({ error: 'Not found' });
          return;
        }
      } else {
        res.status(404).json({ error: 'Not found' });
        return;
      }

      const absoluteFilePath = await realpathAsync(filePath);
      res.setHeader('Content-Type', contentType(file.name) || 'text/plain; charset=utf-8');
      res.status(200).sendFile(absoluteFilePath);
    } catch (error) {
      console.error('Error in getFile:', error);
      res.status(500).json({ error: 'Internal server error' });
    }
  }
}

